#content {
  position: relative;
  z-index: 15;

  max-width: 1280px;
  margin: 0 auto;
  padding: 40px 0;
}

#planes {
  overflow: hidden;
  opacity: 0;
  position: relative;
  transition: opacity 0.5s ease-in;
  padding: 3rem 0;
  z-index: 2;
  background: linear-gradient(94.33deg, #f3f0ff 0.58%, #fff2db 100%);
}

.planes-loaded #planes {
  opacity: 1;
}

.plane-wrapper {
  width: 46%;
  float: left;
  position: relative;
  margin: 0.3rem;
}

.plane-inner {
  padding: 20px;
  box-sizing: border-box;
}

.plane {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  /* box-shadow: inset 0 0 10px #dddddd; */
  z-index: 2;
}

.plane img {
  display: none;
}

/*** handling errors ***/

.no-curtains .plane-title {
  z-index: 1;
}

.no-curtains .plane img {
  display: block;
  width: 100%;
  height: 100%;
  border: 2px solid black;
}

@media screen and (max-width: 1320px) {
  #content {
    margin: 0 20px;
  }

  .plane-inner {
    padding: 20px;
  }
}

@media screen and (max-width: 720px) {
  #content {
    margin: 0 10px;
  }

  .plane-wrapper {
    width: 100%;
    float: none;
    margin-bottom: 10px;
  }

  .plane-inner {
    padding: 10px;
  }
}

/* next section */
.next-section,
.next-section .container {
  padding: 4.4rem 0;
  text-align: center;
  background-color: var(--section-bg-color);
  z-index: 3;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
}

.next-section a {
  font-size: 3rem;
  font-weight: 600;
  padding: 1rem;
  color: var(--dark-violet-color);
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  width: fit-content;
}

.next-section a::after {
  position: absolute;
  z-index: 2;
  width: 100%;
  height: 4px;
  transform: scaleX(0);
  transform-origin: right;
  background-color: var(--dark-violet-color);
  content: "";
  bottom: 0;
  left: 0;
  transition: transform 0.5s cubic-bezier(0.65, 0.05, 0.36, 1);
}

.next-section a:hover::after {
  transform-origin: left;
  transform: scaleX(1);
}

/* footer */
footer .footer-btn {
  z-index: 3;
}

/* overlay */
.overlay-s1 {
  position: fixed;
  z-index: 1000;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  background: linear-gradient(91.9deg, #c9ffff 0.11%, #ffe4f9 100%);
}

.overlay-s2 {
  position: fixed;
  z-index: 999;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  background-color: violet;
}

